#include <iostream>
#include <string>
#include <cstdlib>
#include <vector>

int main(int argc, char *argv[])
{
    std::string number =   "";

    std::vector<char> chars(number.c_str(), number.c_str() + number.size()+1u);
    std::cout << "First value is " << chars[0] << " as int " << int(chars[0])-48 << "\n";
    int prod = 1; 
    for(int i=0; i < 5; i++)
    {
        prod *= (int(chars[i])-48);
    }
    int max_prod = 0;
    int last_zero = -1;
    std::cout << "Initial product is " << prod << "\n";
    for(int i=5; i < chars.size()-1; i++)
    {
        int curr_val = int(chars[i])-48;
        // account for the zero case
        if(curr_val==0)
        {
            last_zero = i;
            prod=1;
            continue;
        }
        
        if(i-5 > last_zero)
        {
          int old_val = int(chars[i-5])-48;
          prod = prod/old_val;
        }

        prod = prod*curr_val;

        std::cout << "Iteration: " << i << " Product: " << prod << "\n";
        if(prod > max_prod)
            max_prod = prod;
    }
    std::cout << "Maximum product is " << max_prod << "\n";
}
